cy.visit('/register');
const loginInput = () => cy.get('input').first();
const passwordInput = () => cy.get('input[type=password]').first();
const confirmInput = () => cy.get('input[type=password]').eq(1);
const birthYearInput = () => cy.get('input[type=number]');
const errorMessage = () => cy.get('.invalid-feedback');
cy.get('button').should('be.visible').and('be.disabled');
loginInput().type('c');
loginInput().clear();
errorMessage().should('be.visible').and('contain', 'The login is required');
loginInput().type('ced');
errorMessage().should('not.exist');
passwordInput().type('p');
passwordInput().clear();
errorMessage().should('be.visible').and('contain', 'The password is required');
passwordInput().type('pa');
errorMessage().should('not.exist');
confirmInput().type('p');
confirmInput().clear();
errorMessage().should('be.visible').and('contain', 'The password confirmation is required');
confirmInput().type('p');
errorMessage().should('be.visible').and('contain', 'The password confirmation does not match');
confirmInput().type('a');
errorMessage().should('not.exist');
birthYearInput().clear();
errorMessage().should('be.visible').and('contain', 'The birth year is required');
birthYearInput().type(`${new Date().getFullYear() - 17}`);
errorMessage().should('be.visible').and('contain', `You're not old enough to bet on ponies`);
birthYearInput().clear();
birthYearInput().type('1986');
errorMessage().should('not.exist');
cy.get('form > button').click();
cy.wait('@registerUser');
cy.location('pathname').should('eq', '/');

storeUserInLocalStorage();
cy.visit('/races');
cy.wait('@getRaces');
// go to bet page for the first race
cy.get('.btn-primary').first().click();
cy.wait('@getRace').its('request.headers').should('have.property', 'authorization', `Bearer ${user.token}`);
cy.location('pathname').should('eq', '/races/12');
// race detail should be displayed
cy.get('h1').should('contain', 'Paris');
cy.get('p').should('contain', 'ago');
cy.get('img').should('have.length', 5);
// no pony is selected
cy.get('.selected').should('have.length', 0);
// bet on first pony
cy.get('img').first().click();
cy.wait('@betRace').its('request.body').should('contain', { ponyId: 1 });
// a pony is now selected
cy.get('.selected').should('have.length', 1);
// bet on the second one
cy.intercept('POST', 'api/races/12/bets', { ...race, betPonyId: 2 }).as('secondBetRace');
cy.get('img').eq(1).click();
cy.wait('@secondBetRace').its('request.body').should('contain', { ponyId: 2 });
// a pony is still selected
cy.get('.selected').should('have.length', 1);
// bet fails
cy.intercept('POST', 'api/races/12/bets', {
    statusCode: 404
}).as('failedBetRace');
// cancel fails
cy.intercept('DELETE', 'api/races/12/bets', {
    statusCode: 404
}).as('failedCancelBetRace');
// cancel bet on second pony
cy.get('img').eq(1).click();
cy.wait('@failedCancelBetRace');
// alert should be displayed
cy.get('.alert').should('contain', 'The race is already started or finished');
// close alert
cy.get('.alert button').click();
cy.get('.alert').should('not.exist');
// bet on first pony
cy.get('img').first().click();
cy.wait('@failedBetRace');
// alert should be displayed
cy.get('.alert').should('contain', 'The race is already started or finished');
// close alert
cy.get('.alert button').click();
cy.get('.alert').should('not.exist');
cy.intercept('DELETE', 'api/races/12/bets', {}).as('cancelBetRace');
// cancel bet
cy.get('img').eq(1).click();
cy.wait('@cancelBetRace');
// no pony is selected anymore
cy.get('.selected').should('have.length', 0);
